CREATE TABLE `admin` (
 `id` int(11) NOT NULL AUTO_INCREMENT,
 `ten` varchar(70) NOT NULL,
 `email` varchar(70) NOT NULL,
 `matkhau` varchar(70) NOT NULL,
 PRIMARY KEY (`id`)
);
CREATE TABLE `nhanvien` (
 `id` int(11) NOT NULL AUTO_INCREMENT,
 `ten` varchar(70) NOT NULL,
 `email` varchar(70) NOT NULL,
 `sodienthoai` varchar(70) NOT NULL,
 `matkhau` varchar(70) NOT NULL,
 `luong` int(11) NOT NULL,
 PRIMARY KEY (`id`)
);
CREATE TABLE `sanpham` (
 `id` int(11) NOT NULL AUTO_INCREMENT,
 `image` varchar(70) NOT NULL,
 `name` varchar(70) NOT NULL,
 `price` int(11) NOT NULL,
 `description` text NOT NULL,
 PRIMARY KEY (`id`)
);
CREATE TABLE `tintuc` (
 `id` int(11) NOT NULL AUTO_INCREMENT,
 `title` text NOT NULL,
 `description` text NOT NULL,
 `detail` text NOT NULL,
 `image` varchar(60) NOT NULL,
 PRIMARY KEY (`id`)
);
CREATE TABLE `users` (
 `id` int(11) NOT NULL AUTO_INCREMENT,
 `hoten` varchar(60) NOT NULL,
 `ngaysinh` varchar(60) NOT NULL,
 `gioitinh` varchar(10) NOT NULL,
 `email` varchar(60) NOT NULL,
 `matkhau` varchar(60) NOT NULL,
 PRIMARY KEY (`id`)
);
INSERT INTO `admin` (`id`, `ten`, `email`, `matkhau`) VALUES (NULL, 'Nguyễn Admin', 'admin@gmail.com', '12345678');
INSERT INTO `nhanvien` (`id`, `ten`, `email`, `sodienthoai`, `matkhau`, `luong`) VALUES (NULL, 'Nguyễn Phúc Lộc', 'npl52000025@gmail.com', '0777779459', 'nguyenphuclo', '7000000'), (NULL, 'Nguyễn Tấn Lực', 'ntl52000078@gmail.com', '0878272222', '12345678', '7000000'), (NULL, 'Nguyễn Minh Hiế', 'a@gmail.com', '0355554646', '12345678', '65000000');
INSERT INTO `sanpham` (`id`, `image`, `name`, `price`, `description`) VALUES (NULL, 'img/1_toii.png', 'Tỏi', '70000', 'Tỏi và các chế phẩm từ tỏi có nhiều công dụng tốt như: chống ung thư, chống oxy hóa, chống đái tháo đường, bảo vệ, chống xơ vữa động mạch, kháng khuẩn, kháng nấm, chống động vật nguyên sinh và hạ huyết áp các hoạt động. Ngoài có nhiều công dụng, tỏi còn là một gia vị không thể thiếu với người Việt.'), (NULL, 'img/2_suplo.png', 'Súp lơ', '40000', 'Súp lơ là loại rau có lượng calorie thấp nhưng lại giàu khoáng chất. Bạn có thể ăn loại rau này thường xuyên để nhận được nhiều lợi ích sức khỏe từ loại rau này mà không sợ tăng cân.'), (NULL, 'img/3_khoqua.png', 'Khổ qua', '40000', ' Khổ qua là một cây leo mọc ở vùng nhiệt đới và cận nhiệt đới thuộc họ Bầu bí, có quả ăn được, thuộc loại đắng nhất trong các loại rau quả. Khổ qua là cây bản địa của vùng nhiệt đới nhưng không rõ có nguồn gốc ở nước nào.'), (NULL, 'img/4_caibexanh.png', 'Cải bẹ xanh', '55000', 'Cải bẹ xanh hay cải xanh, cải canh, cải cay, giới tử là một loài thực vật thuộc họ Cải. Cây có các tên gọi trong tiếng Anh là brown mustard, green mustard, oriental mustard hay vegetable mustard.'), (NULL, 'img/5_cachua.png', 'Cà Chua', '23000', 'Là một loại rau quả làm thực phẩm. Quả ban đầu có màu xanh, chín ngả màu từ vàng đến đỏ. Cà chua có vị hơi chua và là một loại thực phẩm bổ dưỡng, tốt cho cơ thể, giàu vitamin C và A, đặc biệt là giàu lycopeme tốt cho sức khỏe.'), (NULL, 'img/6_bapcaitim.png', 'Bắp cải tím', '39000', 'Bắp cải tím là một loại thực phẩm có chứa hàm lượng vitamin C dồi dào. Vitamin C là một chất oxy hóa, giúp kích thích hoạt động của các tế bào máu, bảo vệ cơ thể khỏi các gốc tự do và tăng cường hệ miễn dịch.'), (NULL, 'img/7_dautay.png', 'Dâu Tây', '199000', 'Dâu tây là một nguồn cung cấp chất chống oxy hóa và chất dinh dưỡng có tác dụng bảo vệ sức khỏe mạnh mẽ. Chúng có hàm lượng calo thấp, giàu chất xơ và dồi dào chất chống oxy hóa và polyphenol.\r\n\r\nMột cốc dâu tây chứa hơn 100% mục tiêu tối thiểu hàng ngày đối với vitamin C hỗ trợ miễn dịch. Ngoài chức năng như một chất chống oxy hóa phòng chống lại bệnh tật và tuổi tác, vitamin C còn giúp tạo ra collagen và duy trì sức khỏe của da.'), (NULL, 'img/8_kiwi.png', 'Kiwi', '239000', 'Kiwi chứa một loại vitamin hiếm, có khả năng tan trong dầu là vitamin E. Đây là một chất chống oxy hóa mạnh, đã và đang được nghiên cứu với tác dụng chống lại bệnh ung thư. Khi cơ thể bị mất cân bằng oxy hóa, có thể làm ảnh hưởng đến DNA và gây ra các vấn đề về sức khỏe. Vì thế, khi dùng kiwi thường xuyên, bạn sẽ tránh được những tổn thương DNA gây ra.'), (NULL, 'img/9_taouc.png', 'Táo Úc', '150000', 'Táo nhập khẩu Úc NFGROUP rất đẹp mắt với vỏ màu đỏ và thịt quả màu vàng với vị chua ngọt đậm đà mùi vị thơm và hương vị giữ rất lâu trong miệng sau khi ăn'), (NULL, 'img/10_Cherry.png', 'Cherry', '379000', 'Cherry là một trong số ít các loại trái cây có chứa melatonin, một loại hormone có tác dụng giữ cho chu kỳ thức-ngủ được ổn định. Một nghiên cứu tại Mỹ cũng chỉ ra rằng những người mắc chứng mất ngủ uống 2 cốc nước ép Cherry vào buổi sáng và tối có thời lượng ngủ tăng thêm trung bình là 1 giờ 24 phút.'), (NULL, 'img/11_cammy.png', 'Cam Mỹ', '339000', 'Cam Mỹ là trái cây có chứa nhiều vitamin C. Hàm lượng vitamin C cao đến mức chỉ cần ăn một trái cam cũng đáp ứng được 130% nhu cầu vitamin C hằng ngày của một người bình thường. Vitamin C trong trái cam Navel còn là thành phần cần thiết cho quá trình tạo liên kết mô, tang cường sức đề kháng và chống viêm.\r\nLoại cam vàng này ăn vào giúp cải thiện sự tươi trẻ cho làn da, chống lão hóa, giảm cholesterol, có tác dụng hồi phục sức khỏe nhanh, tăng cường chức năng tạo hồng huyết cầu, giảm căng thẳng thần kinh.\r\nĂn cam thường xuyên sẽ giúp cơ thể tránh được những bệnh do virus, giảm đáng kể nguy cơ mắc bệnh sỏi thận'), (NULL, 'img/12_xalachmo.png', 'Xà lách mỡ', '339000', 'Trong Đông y, xà lách mỡ được biết đến là loại rau có vị đắng ngọt, tính mát. Có tác dụng giải nhiệt, lọc máu, kích thích tiêu hóa, chống ho, giảm đau, gây ngủ...');
INSERT INTO `tintuc` (`id`, `title`, `description`, `detail`, `image`) VALUES (NULL, 'Trồng ngô nếp chống hạn thu 250 tỷ đồng một năm', 'Ngô nếp đang trở thành cây trồng hàng hóa cho thu nhập cao. Tại Nghi Lộc (Nghệ An) mỗi năm cây ngô trồng được 2 đến 3 vụ, doanh thu đạt 250 tỷ đồng một năm.', '<h1 style=\"text-align: center;\">Trồng ngô nếp chống hạn thu 250 tỷ đồng một năm</h1>\r\n <p class=\"detail\">Ngô nếp đang trở thành cây trồng hàng hóa cho thu nhập cao. Tại Nghi Lộc (Nghệ An) mỗi năm cây ngô trồng được 2 đến 3 vụ, doanh thu đạt 250 tỷ đồng một năm.</p>\r\n <p class=\"detail\">Chị Hà Thị Kiệm ở xóm 11, xã Nghi Mỹ (Nghệ An) trồng ngô nếp trên 2 sào đất ruộng. Chị cho biết: “Thường thì những bắp to, đẹp tôi bán tại ruộng với giá 3.500 đồng một bắp, còn mua ngang thì khoảng 2.500 đồng một bắp. Sau khi trừ chi phí phân,\r\n giống, công sản xuất, 2 sào ngô nếp thu về 10 triệu đồng một vụ. Tính ra năng suất cao gấp 2,5 lần so với trồng lúa\".</p>\r\n <img class=\"center\" src=\"img/detailtt1.jpg\" title=\"Ngô nếp dễ trồng giá cao\">\r\n <p class=\"detail\">Xã Nghi Long là một trong những địa phương có diện tích trồng ngô nếp hàng hoá tương đối nhiều, với diện tích 30 ha, giá trị ước đạt trên 2,8 tỷ đồng một vụ. Ông Nguyễn Tứ Ngọc, Phó chủ tịch Ủy ban nhân dân xã Nghi Long cho biết, đây là một hướng\r\n phát triển kinh tế đem lại hiệu quả kinh tế cao cho bà con nông dân. Bởi vậy, người dân trong xã hào hứng chuyển đổi sang trồng ngô nếp hàng hoá.</p>\r\n <p class=\"detail\">Ngô nếp Nghi Lộc là món ăn đặc sản đối với du khách khi đến Cửa Lò và Thành phố Vinh. Vụ xuân này toàn huyện trồng 800 ha, tăng 200 ha so với vụ xuân năm 2015. Được thu hoạch đúng vào dịp mùa du lịch biển Cửa Lò nên lượng khách tiêu thụ nhiều\r\n và đây cũng là kênh quảng bá cho ngô nếp Nghi Lộc.</p>\r\n <p class=\"detail\">Giống ngô nếp được bà con nông dân Nghi Lộc trồng trong vụ xuân này chủ yếu là MX2; MX4; MX10; W44. Đây là giống ngô nếp có tiềm năng, năng suất cao, có nhiều ưu điểm như: bắp to, hạt dẻo, hạt trên đầu trái bắp đầy đặn và đều từ trong ra ngoài.</p>\r\n <p class=\"detail\">Thời gian sinh trưởng ngắn (khoảng 70 - 80 ngày), ngắn hơn trồng ngô đỏ lấy hạt 40 ngày. Với tổng diện tích 800 ha, sau khi trừ chi phí phân, giống, công chăm sóc, vụ xuân này Nghi Lộc thu về gần 100 tỷ đồng từ trồng ngô nếp hàng hoá. Mỗi năm\r\n cây ngô trồng được từ 2 đến 3 vụ, doanh thu đạt 250 tỷ đồng một năm.</p>\r\n <p class=\"detail\">Ngô nếp trồng ở chất đất Nghi Lộc vừa dẻo vừa có vị thơm ngọt nên được thị trường trong và ngoài huyện ưa chuộng. Do đó, khi vào mùa thu hoạch các thương lái khắp nơi tìm đến tận chân ruộng để chọn mua.</p>\r\n <p class=\"detail\">Một số gia đình tự đưa ngô đi nhập cho các chợ trên địa bàn thành phố Vinh với giá cao hơn từ 3.500 đến 4.000 đồng một bắp.</p>\r\n <p class=\"detail\">Diện tích ngô nếp của Nghi Lộc chủ yếu tập trung nhiều các xã như: Nghi Mỹ, Nghi Trường, Nghi Thịnh, Nghi Hợp, Nghi Khánh, Nghi Long, Nghi Phong, Nghi Trung, Nghi Xá.</p>\r\n <p class=\"detail\">Ông Nguyễn Xuân Quang, Trưởng phòng NN&PTNT huyện cho biết, năng suất ngô nếp năm nay ở Nghi Lộc trung bình đạt từ 18 tấn bắp tươi một ha, cao hơn vụ xuân năm 2015 từ 1 đến 1,5 tấn một ha. Huyện đang chỉ đạo bà con vừa thu hoạch ngô vụ xuân vừa\r\n kết hợp làm đất tiếp tục trồng ngô nếp vụ hè thu. Đồng thời, chuyển 400 ha đất lúa không chủ động nước chuyển sang trồng ngô hè thu.</p>\r\n <i style=\"float: right;\">Theo báo Nghệ An</i>', 'img/tintuc1.jpg'), (NULL, 'Quýt đặc sản Lạng Sơn được giá, hút hàng', 'Mỗi kg quýt Bắc Sơn có giá trung bình 15.000 đồng, ổn định nhiều năm, được thương lái đến tận vườn thu mua.', '<h1 style=\"text-align: center;\">Quýt đặc sản Lạng Sơn được giá, hút hàng</h1>\r\n <p class=\"detail\">Mỗi kg quýt Bắc Sơn có giá trung bình 15.000 đồng, ổn định nhiều năm, được thương lái đến tận vườn thu mua.</p>\r\n <p class=\"detail\">Từ đầu tháng 10 âm lịch, quýt Bắc Sơn (huyện Bắc Sơn, Lạng Sơn) bắt đầu cho thu hoạch. Đây là loại quả đặc sản giúp người dân xóa đói giảm nghèo trong nhiều năm qua.</p>\r\n <p class=\"detail\">Ông Bàn Phúc Thắng, Chủ tịch xã Nhất Tiến cho biết, trên địa bàn xã có 2,15ha quýt, trung bình mỗi cây quýt trưởng thành cho thu hoạch từ 50kg đến 1 tấn quả. Tại xã có hơn 20% số hộ dân trồng quýt Bắc Sơn và diện tích tiếp tục được mở rộng. So với mọi năm giá cả không chênh lệch nhiều dù năm nay sản lượng có sụt giảm so với năm ngoái.</p>\r\n <img class=\"center\" src=\"img/detailtt2_1.jpg\" title=\"Quýt Bắc Sơn là đặc sản của Lạng Sơn giá cả ổn định được khách hàng ưa chuộng. Ảnh: Hồng Vân\">\r\n <p class=\"detail\">Quýt Bắc Sơn được trồng trong các thung lũng mà người dân thường gọi là “lân” -để tới được phải vượt qua nhiều đèo, dốc núi đá. Nhà ông Triệu Tiến Ngân (xã Nhất Tiến) có hơn 700 cây quýt trồng từ hơn 10 năm trước, mỗi cây trưởng thành cao khoảng 5m, trồng cây khoảng 5-6 năm mới bói quả, trọng lượng quả trung bình từ 100 đến 150gam.</p>\r\n <p class=\"detail\">“Trước kia ít hộ trồng quýt nhưng vài năm trở lại đây quýt Bắc Sơn được giá, thương lái đến tận vườn tìm mua. Năm nay, vườn quýt nhà tôi không sai quả nhiều như năm ngoái nhưng giá cả vẫn ổn định”, lão nông này chia sẻ.</p>\r\n <img class=\"center\" src=\"img/detailtt2_2.jpg\" title=\"Quýt Bắc Sơn khi chín quả vàng, vỏ mỏng, vị ngọt, ít hạt. Ảnh: Hồng Vân\">\r\n <p class=\"detail\">Ông Ngân cho biết, mỗi kg quýt bán buôn giá dao động từ 13.000 đến 17.000 đồng tùy loại; bán lẻ giá 15.000-20.000 đồng một kg, loại quả to đẹp, vỏ nhẵn vẫn được giá đến 25.000 đồng. Ngay từ tháng 8, thương lái đã đến tận vườn nhà ông đặt mua toàn bộ vườn quýt giá hơn 100 triệu.</p>\r\n <p class=\"detail\">Theo chị Triệu Thị Lụa (xã Nhất Tiến), chăm sóc cây quýt không mất quá nhiều công sức, sau khi thu hoạch có thể bón phân hữu cơ đã hoại mục, không cuốc xới quanh gốc vì rễ cây này ăn lên mặt đất dễ bị đứt.</p>\r\n <p class=\"detail\">Mặc dù thời điểm chín rộ của quýt vào tháng 10 và tháng 11 âm lịch, nhưng nhiều gia đình để dành đến gần Tết mới thu hoạch vì giá bán buôn lên đến 35.000-40.000 đồng một kg.</p>\r\n <p class=\"detail\">Thống kê của ngành nông nghiệp, toàn huyện Bắc Sơn có khoảng 500 ha quýt. Nhận thấy giá trị kinh tế cao nên vài năm gần đây người dân mở rộng diện tích trồng. Thương lái Nông Thị Hợi (Thái Nguyên) cho hay, quýt Bắc Sơn khi chín ăn ngọt, vỏ quả căng bóng, ít sơ, ít hạt nên khách đặt mua nhiều.</p>\r\n <p class=\"detail\">“Loại quýt này không để được lâu nên tôi thường gọi điện trước rồi buổi chiều đánh xe tải đến cân hàng, sáng sớm hôm sau gửi xe khách đi TP Lạng Sơn, Thái Nguyên, Hà Nội”, chị Hợi nói.</p>\r\n <i style=\"float: right;\">Hồng Vân</i>', 'img/tintuc2.jpg'), (NULL, 'Nhiều giống lê Nhật Bản có mặt tại Việt Nam', 'Các trái lê của vùng Ibaraki - Tây Bắc Kanto (Nhật Bản) vừa được giới thiệu đến người tiêu dùng tại TP HCM vào sáng ngày 23/8.', '<h1 style=\"text-align: center;\">Nhiều giống lê Nhật Bản có mặt tại Việt Nam</h1>\r\n <p class=\"detail\">Các trái lê của vùng Ibaraki - Tây Bắc Kanto (Nhật Bản) vừa được giới thiệu đến người tiêu dùng tại TP HCM vào sáng ngày 23/8.</p>\r\n <p class=\"detail\">Đơn vị phân phối sản phẩm tại Việt Nam là Doanh nghiệp tư nhân Sản xuất Thương mại Dịch vụ Tú Phượng vừa tổ chức buổi giới thiệu tại Trung tâm thương mại Takashimaya, 65 Lê Lợi, phường Bến Nghé, quận 1, TP HCM.</p>\r\n <p class=\"detail\">Sự kiện với các hoạt động dùng thử sản phẩm, chụp ảnh lưu niệm, bán giá ưu đãi.</p>\r\n <p class=\"detail\">Giống lê đầu tiên của Ibaraki được xuất khẩu sang Việt Nam đợt này là Kousui của thành phố Shitotsuma. Đây là sản phẩm chiếm 40% sản lượng lê tại Nhật Bản và được nhiều người tiêu dùng nước này yêu thích nhất.</p>\r\n <img class=\"center\" src=\"img/detailtt3_1.jpg\" title=\"Nhà phân phối cùng chuyên gia Nhật Bản tại lễ ra mắt sản phẩm ở TP HCM sáng nay.\">\r\n <p class=\"detail\">Theo nhà phân phối, khu vực Ibaraki (Tây Bắc Kanto, Nhật Bản) là nơi có sản lượng quả lê lớn thứ hai quốc gia này. Khí hậu ở đây có sự chênh lệch nhiệt độ lớn giữa ngày và đêm; nguồn nước dồi dào cùng tính chất đất phù hợp với việc trồng quả lê.</p>\r\n <p class=\"detail\">Ông Kawwada - nguyên Giám đốc Cục kinh doanh và xúc tiến thương mại thuộc Bộ Nông nghiệp Nhật Bản chia sẻ, thời gian qua, chính quyền vùng Ibaraki có mối quan hệ thân mật với các địa phương Việt Nam như: tỉnh Nam Định, Đồng Tháp và Hà Nam trong việc hợp tác về lĩnh vực nông nghiệp.</p>\r\n <p class=\"detail\">Hồi đầu năm nay, Việt Nam đã chấp thuận việc nhập khẩu quả lê Nhật vào thị trường. Ngoài chuyến lê đầu tiên của đợt này, trong tháng 9, các giống lê khác như Hosui, Akizuki và Niitaka sẽ chuyển đến Việt Nam; giúp người tiêu dùng có cơ hội thưởng thức các hương vị khác nhau.</p>\r\n <img class=\"center\" src=\"img/detailtt3_2.jpg\" title=\"Lê Nhật Bản có chất lượng cao nhờ khí hậu, thổ nhưỡng cùng phương pháp canh tác sáng tạo từ người trồng.\">\r\n <p class=\"detail\">Để có những trái lê chất lượng, người trồng lê sử dụng thiết bị cảm biến ánh sáng để kiểm tra hàm đường. Các sản phẩm lê của Shitotsuma cũng có mặt tại thị trường nước ngoài như: Malaysia, Thái Lan, Singapore...</p>\r\n <p class=\"detail\">Shitotsuma đang tập trung phát triển thương hiệu lê của riêng thành phố. Những trái lê Kousui chín được lựa chọn kỹ lưỡng và hái từ trên cây có tên “lê Shitotsuma Kanjyuku”.</p>\r\n <p class=\"detail\">Tại Nhật Bản, cùng với vị ngon, yêu cầu về độ an toàn của người tiêu dùng với trái lê khá cao.</p>\r\n <img class=\"center\" src=\"img/detailtt3_3.jpg\" title=\"Các nhà sản xuất lê tại Nhật Bản cũng gặp phải sự canh tranh gay gắt trên thị trường.\">\r\n <p class=\"detail\">Bên cạnh đó, sự cạnh tranh giữa các nhà sản xuất cũng rất khốc liệt. Họ có nhiều ý tưởng và bỏ ra công sức hàng năm trời để canh tác giống cây này. Dù phương thức sản xuất có sự khác biệt theo từng giống lê nhưng thời gian thu hoạch và đưa hàng ra thị trường bắt đầu từ tháng 8 đến tháng 10 hàng năm.</p>\r\n <p class=\"detail\">Dựa vào màu vỏ, trái lê Nhật Bản, gồm hai loại chính là “Lê đỏ” và “Lê xanh”. Vị của hai loại lê này khác nhau; trong khi lê đỏ ngọt và mọng nước thì lê xanh lại có vị mát, dịu nhẹ, giòn tan.</p>\r\n <p class=\"detail\">Tại Nhật Bản, người dân phát hiện ra lê có thể ăn được từ thời Yayoi (khoảng thế kỷ X trước Công nguyên - giữa thế kỷ III sau Công nguyên). Trong cuốn sách sử “Nhật Bản thư kỷ” thời Nara (710-794) cũng ghi chép lại việc canh tác lê rất được khuyến khích. Đến thời Edo (1603- 1868), có hơn 150 giống lê được lai tạo tại quốc gia này.</p>\r\n <i style=\"float: right;\">Mai Thương</i>', 'img/tintuc3.jpg'), (NULL, 'Những nông sản tăng giá đột biến vì Trung Quốc thu gom', 'Nếu năm ngoái giá thanh long, chuối rớt thê thảm thì hiện nay lại tăng cao kỷ lục.', '<h1 style=\"text-align: center;\">Những nông sản tăng giá đột biến vì Trung Quốc thu gom</h1>\r\n <p class=\"detail\">Nếu năm ngoái giá thanh long, chuối rớt thê thảm thì hiện nay lại tăng cao kỷ lục.</p>\r\n <p class=\"detail\"><b>1. Thanh Long</b></p>\r\n <p class=\"detail\">Là nông sản có giá bấp bênh và phụ thuộc nhiều vào thị trường Trung Quốc, đầu năm nay thay vì giảm thì thanh long tăng giá cao.</p>\r\n <img class=\"center\" src=\"img/detailtt4_1.jpg\" title=\"Giá thanh long đỏ được thu mua tới 40.000 đồng mỗi kg ngay tại vườn.\">\r\n\r\n <p class=\"detail\">Anh Phú, người có 9 sào thanh long theo tiêu chuẩn VietGAP ở (Bình Thuận) cho hay, đầu tháng 2 thương lái đến đặt hàng khá sớm để bán sang Trung Quốc. Họ thu mua với giá 25.000 đồng một kg. Nhờ vậy, vụ năm nay anh thu lãi 100 triệu đồng.</p>\r\n <p class=\"detail\">Trong khi đó, chị Hạnh, người có 4 sào thanh long ruột đỏ ở Tiền Giang, còn được thu mua với giá 40.000 đồng mỗi kg tại vườn. “Nếu năm trước mặt hàng này rớt giá thì nay thương lái Trung Quốc mua nhiều nên tăng cao. Nhờ vậy tôi cũng lãi được 60 triệu đồng”, chị Hạnh nói.</p>\r\n <p class=\"detail\">Theo các nhà vườn, chỉ cần giá thanh long bình ổn ở mức 12.000 -13.000 đồng một kg, nông dân đã có lời.</p>\r\n <p class=\"detail\">Trao đổi với VnExpress, ông Huỳnh Hồng Ửng, Chủ nhiệm Hợp tác xã thanh long Chợ Gạo (Tiền Giang) cho biết, tuần vừa rồi thanh long ruột đỏ được mua với giá 40.000 đồng một kg tại vườn. Thanh long trắng giá 15.000-16.000 đồng.</p>\r\n <p class=\"detail\">Tuy nhiên, ông Ửng cho biết, giá cao nên có tình trạng nhà vườn chặt thanh long trắng và trồng thanh long đỏ. “Tôi lo sợ nếu trồng với số lượng ồ ạt thì giá thanh long đỏ lại có nguy cơ rớt nhanh trong thời gian tới”, ông Ửng nói.</p>\r\n <p class=\"detail\"><b>2. Chuối</b></p>\r\n <p class=\"detail\">Chị Hằng, người có một ha chuối ở Đồng Nai, năm nay rất phấn khởi vì được thương lái đến tận vườn gom với giá 15.000 đồng một kg, cao gấp nhiều lần so với năm ngoái.</p>\r\n <img class=\"center\" src=\"img/detailtt4_2.jpg\" title=\"Năm nay, chuối tại Tiền Giang chủ yếu xuất sang Trung Quốc.\">\r\n <p class=\"detail\">Là doanh nghiệp trồng xuất khẩu, ông Trần Danh Thế, chủ sở hữu 30 ha chuối ở Đồng Nai cho biết, quý I năm nay giá tăng cao kỷ lục, có lúc lên tới 18.000 đồng một kg.</p>\r\n <p class=\"detail\">Nguồn chuối xuất khẩu hụt hàng, nhiều thương lái đẩy giá để thu gom cho đủ chuyến. Mặt khác, năm nay tại Trung Quốc lượng hàng khan hiếm, nên phải gia tăng gom hàng Việt. “Chúng tôi đã nhận được khá nhiều đơn hàng đến từ nhiều nước nhưng lượng hàng cung ứng không đủ nên tạm thời nhận đơn xuất đi Trung Quốc bởi họ đưa ra mức giá phù hợp”, ông Thế nói.</p>\r\n <p class=\"detail\">Theo Sở Nông nghiệp & Phát triển Nông thôn tỉnh Đồng Nai, toàn tỉnh có khoảng 7.000 ha diện tích trồng chuối, trong đó chuối già hương chiếm khoảng 45%, chủ yếu xuất sang Trung Quốc.</p>\r\n <p class=\"detail\">Không chỉ ở khu vực Đồng Nai mà tại các tỉnh phía Bắc, giá chuối cũng tăng hơn so với năm ngoái nhờ Trung Quốc gom hàng. Ông Trần Văn Căn, chủ cơ sở sản xuất chuối tại Hưng Yên cho biết, tại khu vực phía Bắc giá thu mua là 8.000 -10.000 đồng một kg. “Nếu năm ngoái cùng thời điểm này tôi xuất khoảng 200 tấn thì nay mới được 60 tấn. Lượng hàng khá khan hiếm”, ông Căn nói và cho biết thêm, Trung Quốc vẫn là thị trường chủ lực của Việt Nam nên giá cả phụ thuộc rất nhiều vào họ. Hiện, người dân vẫn trồng chuối thụ động, không có kế hoạch và kỹ thuật chuẩn nên hàng để xuất sang các thị trường khó tính rất ít.</p>\r\n <p class=\"detail\">Năm ngoái, từ giữa tháng 2, giá chuối già hương cấy mô chạm đáy ở mức 1.000-2.000 đồng một kg. Nhiều nông dân ở Đồng Nai khóc ròng vì chuối chín rục trên cây cũng không ai mua, đành ngậm ngùi bán rẻ cho người nuôi dê.</p>\r\n <p class=\"detail\"><b>3. Mít</b></p>\r\n <p class=\"detail\">Cũng liên tục tăng cao, mít Thái tại các tỉnh miền Tây có đợt lên tới 45.000 đồng một kg.</p>\r\n <img class=\"center\" src=\"img/detailtt4_3.jpg\" title=\"Lượng mít ngon đều được thương lái Trung Quốc săn đón.\">\r\n <p class=\"detail\">Ông Bảy Ẩn, ở huyện Cai Lậy, Tiền Giang cho biết, đây là năm mà mít Thái có giá cao nhất từ trước tới nay. Năm nay, ông Bảy Ẩn thu hoạch 5 tấn, giá 43.000 đồng một kg cho loại một (trái trên 9kg) và 35.000 đồng một kg cho loại 2 (trái trên 7kg). Ông Bảy Ẩn cho biết, khi mới trồng, mít Thái loại 1 giá 15.000 đồng một kg thì nay chỉ với hàng dạt, ông đã bán được với giá này.</p>\r\n <p class=\"detail\">“Chưa có đầu năm nào thuận lợi như năm nay. Thương lái liên tục đi gom hàng tận nhà và trả với giá cao nhưng không còn hàng để bán. Có ngày có 3-4 người hỏi nhưng đều hết hàng sớm”, ông Bảy chia sẻ thêm.</p>\r\n <p class=\"detail\">Không chỉ ở Tiền Giang, Bến Tre, nhà vườn bán được mít với giá cao mà tại Cần Thơ, Hậu Giang cũng lãi lớn với mít Thái.</p>\r\n <p class=\"detail\">Lý giải cho giá mít tăng cao, hầu hết nhà vườn và thương lái cho biết, nếu như những năm trước, Trung Quốc chỉ thu mua trái mít đã tách múi, bỏ hạt và đóng vào hộp thì nay lại tăng mua mít nguyên trái. Mặt khác, do chưa phải là chính vụ nên giá cao hơn so với thời kỳ vào mùa.</p>\r\n <p class=\"detail\">Theo Sở Nông nghiệp & Nông thôn tỉnh Tiền Giang, giá mít loại một bán tại vườn đang trên 40.000 đồng một kg, còn tại chợ là 60.000 đồng. Đây là mức cao kỷ lục so với 5 năm trở lại đây. Tuy nhiên, theo nhìn nhận của các chuyên gia nông nghiệp, khi giá tăng đột biến, diện tích trồng mít sẽ tiếp tục tăng lên. Nếu diện tích trồng tăng với tốc độ nhanh và không có định hướng, lại xuất hiện nguy cơ cung vượt cầu. Mặt khác, Trung Quốc ngừng thu mua sẽ khiến giá giảm mạnh. Vì vậy, người dân cần cân nhắc và cẩn trọng khi tăng diện tích.</p>\r\n <i style=\"float: right;\">Hồng Châu</i>', 'img/tintuc4.jpg'), (NULL, 'Dưa Trung Quốc giá rẻ đổ vào Việt Nam', 'Mỗi năm có hàng nghìn tấn dưa Trung Quốc với đủ chủng loại nhập vào Việt Nam.', '<h1 style=\"text-align: center;\">Dưa Trung Quốc giá rẻ đổ vào Việt Nam</h1>\r\n <p class=\"detail\">Mỗi năm có hàng nghìn tấn dưa Trung Quốc với đủ chủng loại nhập vào Việt Nam.</p>\r\n <p class=\"detail\">Chị Hoa, tiểu thương chợ An Bình (quận 5, TP HCM) cho biết, hơn một tháng nay lượng dưa về chợ khá nhiều với đủ các mẫu mã. Trong đó, dưa lê và dưa lưới có giá rẻ hơn so với bình thường.</p>\r\n <p class=\"detail\">“Nếu thông thường dưa vàng quả tròn có giá 60.000 đồng một kg thì nay loại dưa lưới quả dài mẫu mã đẹp cũng chỉ 25.000 - 40.000 đồng. Vì có giá hấp dẫn nên khách mua nhiều hơn”, chị Hoa nói và cho biết, loại dưa lưới này đa phần nguồn gốc từ Trung Quốc, có nhiều từ tháng 6 trở đi. Loại trái này khá dễ phân biệt vì dưa Việt trái nhỏ và không đồng đều. Còn dưa lê Trung Quốc trái to và mẫu mã đồng đều nhau.</p>\r\n <img class=\"center\" src=\"img/detailtt5.jpg\" title=\"Dưa lê Trung Quốc thường có mẫu mã bắt mắt hơn hàng Việt.\">\r\n <p class=\"detail\">Cũng chuyên bán dưa tại chợ đầu mối Thủ Đức, chị Hòa cho biết, nửa đầu năm nay dưa hoàng kim và dưa lê chiếm đa số. Các loại dưa này năm nào cũng được đưa về Việt Nam theo mùa với số lượng lớn và giá rẻ hơn so với hàng Việt.</p>\r\n <p class=\"detail\">Trao đổi với VnExpress, lãnh đạo chợ đầu mối nông sản Thủ Đức cho biết, thông thường các loại trái cây Trung Quốc trồng nghịch vụ so với hàng Việt nên được nhập về chợ với giá hấp dẫn. Vào tháng 9, 10 dưa lưới vàng được nhập về nhiều, còn dưa lê và dưa hoàng kim thì đang vào vụ nên lượng về chợ có tăng.</p>\r\n <p class=\"detail\">Theo Cục Bảo vệ thực vật, trong năm 2017, Việt Nam nhập tới 7.210 tấn dưa từ thị trường Trung Quốc, trong đó, dưa lưới vàng 3.710 tấn, dưa lưới xanh 3.000 tấn, dưa lê nhập 500 tấn.</p>\r\n <p class=\"detail\">Sang 2018, 5 tháng đầu năm, lượng dưa Trung Quốc đổ về cũng lên tới gần 650 tấn các loại, Theo lãnh đạo Cục Bảo vệ thực vật, sản phẩm khi nhập vào thị trường Việt Nam đều được kiểm định. Tuy nhiên, nhiều tiểu thương đã gắn mác hàng Việt để dễ bán hàng.</p>\r\n <p class=\"detail\">Đặc điểm của dưa lưới vàng Trung Quốc là quả dài hình bầu dục, bên ngoài vỏ màu vàng nặng khoảng 3 - 4 kg một quả. Còn dưa Việt hình tròn, trọng lượng quả chỉ từ 1 - 2 kg. Giá dưa lưới vàng Việt Nam cũng cao gấp đôi dưa lưới vàng Trung Quốc.</p>\r\n <p class=\"detail\">Còn với dưa lê, loại quả này không khó phân biệt vì hàng Trung Quốc trái to 300 - 600 gram một trái, mẫu mã đẹp. Ngược lại, hàng Việt trái nhỏ, hay bị méo.</p>\r\n <i style=\"float: right;\">Hồng Châu</i>', 'img/tintuc5.jpg');
